object Channel {
  type ofChannel = List[Map[Id, ID]]

  type Sensor = ID

  object ObstacleSensor {
    case class Obstacle(val x: Double, val y: Double)
  }

  object ID {
    object Sensor {
      def fromId(id: ID): Sensor = ObstacleSensor.Obstacle(id.x, id.y)
    }
  }

  object ChannelBuilder {
    def newChannel(fromId: Sensor, toId: Sensor, obstacles: List[Obstacle]): Channel = {
      val nodes = Map[Sensor, Id]().foldLeft(Map.empty[Sensor, Id]) {
        case (acc, id) =>
          id match {
            case sourceId => if (sourceId == fromId) id else acc
            case destinationId => if (destinationId == toId) destinationId else acc
            case obstacleId => {
              obstacleId match {
                case sourceId if obstacles.contains(obstacleId) => obstacleId
                case destinationId => destinationId
              }
            }
          }
        }
      }
      newChannel(nodes, toId)
    }
  }

  def main(args: Array[String]): Unit = {
    val channel = ChannelBuilder.newChannel(Sensor("source"), Sensor("destination"), List(ObstacleSensor.Obstacle(10.0, 20.0)))
