  Array.fill(rows * cols)(0)
  for (i <- 0 until rows) {
    for (j <- 0 until cols) {
      if (i > 0) {
        grid(i)(j) += grid(i - 1)(j)
      }
      if (i < rows - 1) {
        grid(i)(j) += grid(i + 1)(j)
      }
      if (j > 0) {
        grid(i)(j) += grid(i)(j - 1)
      }
      if (j < cols - 1) {
        grid(i)(j) += grid(i)(j + 1)
      }
    }
  }
  Array.fill(rows * cols)(grid(i)(j))
}

