  def G[A](source: Boolean, field: A, acc: A => A, metric: () => A): A = {
    val maxInfluence = metric().map(nbrRange()).foldLeft(Double.MaxValue)(Double.lte)
    val potential = G(source, 0, _ + nbrRange(), nbrRange()).map(nbrRange().map(_ >= maxInfluence))
    acc(potential)
