  def nbrmin(P, field: V): List[P] = {
    nbr(P).filter(_._1 < field)
  }

  // Method to get neighbors with IDs between the given IDs
  def nbr(P: Map[Int, Double], field: V): List[P] = {
    nbr(P).map(_._1 >= field) map (_._2)
  }

  // Method to get neighbors with IDs greater than the given ID
  def nbrmax(P: Map[Int, Double], field: V): List[P] = {
    nbr(P).map(_._1 < field) map (_._2)
  }

  // Method to find the minimum value in a network using S
  def S[V](maxInfluence: Double, metric: () => Double): V = {
    val potential = nbrRange().map(nbrRange()).map(_ >= maxInfluence)
    nbrRange().foldLeft(0, 0) {
      case ((acc, val), _) =>
        val newAcc = (acc._1 + val, acc._2 + 1)
        (newAcc, val)
    }
  }

  // Method to find the minimum value in a network using G with a custom metric
  def G[V](source: Boolean, field: V, metric: () => Double, maxInfluence: Double): V = {
    val potential = nbrRange().map(nbrRange()).map(_ >= maxInfluence)
    potential.map(nbrRange().foldLeft(0, 0) {
      case ((acc, val), _) =>
        val newAcc = (acc._1 + val, acc._2 + 1)
        (newAcc, val)
    })
  }

  // Method to get neighbors with IDs greater than the given ID
  def nbrs[V](source: Boolean, field: V, maxInfluence: Double): List[V] = {
    nbrRange().filter(_._1 > field) map (_._2)
  }

  // Method to get neighbors with IDs less than the given ID
  def nbs[V](source: Boolean, field: V, maxInfluence: Double): List[V] = {
    nbrRange().filter(_._1 < field) map (_._2)
  }

  // Method to find the minimum value in a network using C with a custom metric
  def C[V](potential: V, acc: (V, V) => V, local: V, Null: V): V = {
    val potentialPartial = if (potential map(nbrRange).sum >= maxInfluence) {
