object NeighborCounter {

  trait Sensor {
    def sense(temperature: Double): Double
  }

  class nbr(private val data: Double) {
    def sense(sensor: Sensor): Double = sensor.sense(data)
  }

  implicit def nbrCast[A](val sensor: Sensor): nbr => A = _.sense(sensor)
  implicit val nbrRange: nbr => Int = nbr

  def rep[A](initial: A, evolve: (A, Int) => A): A = initial match {
    case right -> evolve(initial, 1)
    case left => rep(right, evolve)
  }

  def branch[A](condition: Boolean)(thenB: A)(elseB: A): A = {
    condition match {
      case true  => thenB
      case false => elseB
    }
  }

  def countNeighbors excluding_self: Int = {
    foldhood(0) {
      case mid, _ =>
        nbrCast nbrRange(mid)(nbrRange + nbrRange(_ - 1))
    }.min
  }

  def main(args: Array[String]): Unit = println(countNeighbors(excluding_self))

  def neighborInfo: Double = {
    val nodes = foldhood(List.empty[Int])(nbrRange)((_, i) => i + 1).filter(_ % 2!= 0).toList
    val temperature = nbrCast[Sensor](nbrRange)((n, i) => sense[Sensor](temperature)(n))(_ + nbrRange(i))
    temperature
  }
}

