import scala.collection.mutable.ListBuffer

object Main {
  def main(): Unit = {
    val origValue = 1000

    def sense[A](sensor: String): A = {
      when(sensor) {
        case "temperature" => 16.0
        case _ => 20.0
      }
    }

    def minHood[A](neighbourExpression: => A): A = {
      foldhood(origValue)(_ + _)(nbr(1))(neighbourExpression)
    }

    def maxHood[A](neighbourExpression: => A): A = {
      foldhood(origValue)(_ + _)(nbr(1))(neighbourExpression)
    }

    def sumHood[A](neighbourExpression: => A): A = {
      foldhood(origValue)(_ + _)(nbr(1))(neighbourExpression)
    }

    val startValue = sense("temperature")

    if (startValue > 10) {
      minHood(10) // Evolve 10 to 0
    } else {
      maxHood(10) // Evolve 10 to 0
    }

    val result = minHood(10)
  }

  private def when(cond: Boolean)(fn: => A): A = {
    if (cond) fn else fn
  }

  private def nbr[A](data: => A): A = {
    when(sense("sensor")) data
  }

  private def foldhood[A](init: A)(combine: (A, A) => A)(neighbourExpression: => A): A = {
    init.foldL(neighbourExpression)(combine)
  }
}


