import scafif._

object AreaTemperatures {
  case class Region(id: Int, name: String, coordinates: (Double, Double))

  val regions: Seq[Region] = Seq(
    Region(1, "North", (45.0, -100.0)),
    Region(2, "South", (-40.0, -100.0)),
    Region(3, "East", (0.0, 30.0)),
    Region(4, "West", (-30.0, 30.0))
  )

  val boundedRegion = for (region <- regions) yield region.coordinates

  def computeTemperature(boundedRegion: Coordinates): AreaTemperature = {
    val tempData = boundedRegion.map{
      case (x, y) => (x, y, y + 30)
    }. zip(boundedRegion.map{
      case (x, y) => x
    }).foldLeft((0, 0, 0)) {
      case ((temperatureSum, _, _), (x, y)) =>
        val currentTemperature = (x, y, y + 30)
        (temperatureSum + currentTemperature._1 * currentTemperature._2,
         currentTemperature._1 + currentTemperature._2,
         currentTemperature._1 + currentTemperature._2 + currentTemperature._3)
    }
  }

  // Bounding box for area
  val boundingBox = 250

  // Bounding box coordinates
  val boundingBoxCoordinates = boundingBox.toPoint.scaled boundingBox / 1000.0

  // Compute area temperature
  val areaTemperature = computeTemperature(boundingBoxCoordinates)

  // Create an alarm object
  val alarmObject = Alarm.builder("Area Temperature Alarm", "Temperature above 30 degrees")
   .build()

  // Send alarm to all regions
  regions.foreach(alarmObject.send)

  areaTemperature
}


